     and close_time IS NOT NULL 
     and TO_DAYS(NOW()) - TO_DAYS(prop6) >= -7 
     and TO_DAYS(NOW()) - TO_DAYS(prop6) >= -14 
     and if(prop8 ='',
        TO_DAYS(NOW()) - TO_DAYS(prop6) >= 0 and prop6 IS NOT NULL
        AND prop6  <>  '',
        (flow1 = '0' or flow2 = '0' or flow3 = '0' or flow4 = '0' or flow5 = '0'))  
     and TO_DAYS(NOW()) - TO_DAYS(prop6) >= 0 and prop6 IS NOT NULL
        AND prop6  <>  ''
     and abnormal_condition IS NOT NULL
        AND abnormal_condition  <>  ''
     and user_id IS NOT NULL
        AND user_id  <>  ''


        and id in

        #{ids}







        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

