monitoring

    user_id IS NOT NULL
    AND user_id  <>  ''
     and id = #{id} 
     and user_id = #{userId} 
     and user_name LIKE CONCAT(CONCAT('%',#{userName}),'%') 
     and anomalous_region = #{anomalousRegion} 
     and customer_id = #{customerId} 
     and customer_name = #{customerName} 
     and abnormal_condition = #{abnormalCondition} 
     and machine_type = #{machineType} 
     and create_time = #{createTime} 
     and update_time = #{updateTime} 
     and abnormal_time = #{abnormalTime} 
    and monitor_code LIKE CONCAT(CONCAT('%',#{monitorCode}),'%') 
     and monitor_mode_code = #{monitorModeCode} 
     and belong_region = #{belongRegion} 
     and belong_floor = #{belongFloor} 
     and cumulative_flow = #{cumulativeFlow} 
     and length = #{length} 
     and param_json = #{paramJson} 
     and status = #{status} 
     and Stall = #{stall} 
     and prop1 = #{prop1} 
     and prop2 = #{prop2} 
     and prop3 = #{prop3} 
     and prop4 = #{prop4} 
     and prop5 = #{prop5} 
     and prop6 = #{prop6} 
     and prop7 = #{prop7} 
