     and prop9 = #{prop9} 
     and prop10 LIKE CONCAT(CONCAT('%',#{prop10}),'%') 
     and TO_DAYS(NOW()) - TO_DAYS(prop6) >= -#{searchTime} 


group BY user_id


        order by ${sort} ${order}


        order by id desc



    limit #{offset}, #{limit}

